using System;

using BepInEx;
using BepInEx.Logging;
using BepInEx.Configuration;

using HarmonyLib;

using UnityEngine;

namespace CustomWave
{
    [BepInPlugin(PluginInfo.PLUGIN_GUID, PluginInfo.PLUGIN_NAME, PluginInfo.PLUGIN_VERSION)]
    [BepInProcess("ULTRAKILL.exe")]
    public class Plugin : BaseUnityPlugin
    {
        internal static ManualLogSource Log;
        internal static Harmony harmony;

        internal static ConfigEntry<int> ConfigCustomWave;
        internal static WaveSetter CustomWaveSetter;
        internal static Sprite CheatIcon;
        
        private void Awake()
        {
            Logger.LogInfo($"Loading Plugin {PluginInfo.PLUGIN_GUID}...");

            Plugin.Log = base.Logger;
            Plugin.Log.LogInfo("Created Global Logger");

            ConfigCustomWave = Config.Bind("General", "CustomWave", 1, "The value of the Custom Wave Setter");
            Plugin.Log.LogInfo("Setup Config");

            byte[] imageBytes = Convert.FromBase64String(CheatIconStr);
            Texture2D tex = new Texture2D(32, 32);
            tex.filterMode = FilterMode.Point;
            tex.LoadImage(imageBytes);

            CheatIcon = Sprite.Create(tex, new Rect(0.0f, 0.0f, tex.width, tex.height), new Vector2(0.5f, 0.5f), 100.0f);

            harmony = new Harmony("CustomWave");

            harmony.PatchAll(typeof(WaveUISetup));
            harmony.PatchAll(typeof(GridColorPatch));

            harmony.PatchAll(typeof(WSSelectedPatch));
            harmony.PatchAll(typeof(WSUnselectedPatch));
            harmony.PatchAll(typeof(WSLockedPatch));
            harmony.PatchAll(typeof(WSPreparePatch));
            
            harmony.PatchAll(typeof(RegisterCheat));
            harmony.PatchAll(typeof(CheatIconPatch));
            harmony.PatchAll(typeof(CheckWaveAvailabilityPatch));
            harmony.PatchAll(typeof(SetCurrentWavePatch));
            harmony.PatchAll(typeof(GetHighestWavePatch));

            Plugin.Log.LogInfo("Applied All Patches");

            Plugin.Log.LogInfo($"Plugin {PluginInfo.PLUGIN_GUID} is loaded!");
        }

        private void OnDestroy()
        {
            harmony?.UnpatchSelf();
        }

        internal static string CheatIconStr = "";
    }
}
